(48, 'woocommerce-WCSubscriptions-June-2023-updated-needed', 'marketing', 'en_US', 'Action required: Security update of WooCommerce Subscriptions', '<strong>Your store requires a security update for the WooCommerce Subscriptions plugin</strong>. Please update the WooCommerce Subscriptions plugin immediately to address a potential vulnerability.', '{}', 'pending', 'woocommerce.com', '2023-09-10 10:01:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(49, 'woocommerce-WCReturnsWarranty-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce Returns and Warranty Requests extension', '<strong>Your store requires a security update for the Returns and Warranty Requests extension</strong>.  Please update to the latest version of the WooCommerce Returns and Warranty Requests extension immediately to address a potential vulnerability discovered on May 31.', '{}', 'pending', 'woocommerce.com', '2023-09-10 10:01:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(50, 'woocommerce-WCOPC-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce One Page Checkout', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce One Page Checkout extension</strong>. The fix for this vulnerability was released for this extension on June 13th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2023-09-10 10:01:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(51, 'woocommerce-WCGC-July-2023-update-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce GoCardless Extension', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce GoCardless extension</strong>. The fix for this vulnerability was released on July 4th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2023-09-10 10:01:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'woocommerce-shipping-fedex-api-outage-2023-07-16', 'warning', 'en_US', 'Scheduled FedEx API outage — July 2023', 'On July 16 there will be a full outage of the FedEx API from 04:00 to 08:00 AM UTC. Due to planned maintenance by FedEx, you\'ll be unable to provide FedEx shipping rates during this time. Follow the link below for more information and recommendations on how to minimize impact.', '{}', 'pending', 'woocommerce.com', '2023-09-10 10:01:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(53, 'wcship-2023-07-hazmat-update-needed', 'update', 'en_US', 'Action required: USPS HAZMAT compliance update for WooCommerce Shipping & Tax extension', '<strong>Your store requires an update for the WooCommerce Shipping extension</strong>. Please update to the latest version of the WooCommerce Shipping &amp; Tax extension immediately to ensure compliance with new USPS HAZMAT rules currently in effect.', '{}', 'pending', 'woocommerce.com', '2023-09-10 10:01:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'woocommerce-WCStripe-Aug-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe plugin', '<strong>Your shop requires an important security update for the  WooCommerce Stripe plugin</strong>. The fix for this vulnerability was released on July 31. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2023-09-10 10:01:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'woocommerce-WCStripe-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooCommerce Stripe plugin', '<strong>Your store has been updated to the latest secure version of the WooCommerce Stripe plugin</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2023-09-10 10:01:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'woocommerce-WooPayments-Aug-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooPayments (WooCommerce Payments) plugin', '<strong>Your shop requires an important security update for the WooPayments (WooCommerce Payments) extension</strong>. The fix for this vulnerability was released on July 31. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2023-09-10 10:01:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'woocommerce-WooPayments-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooPayments (WooCommerce Payments) plugin', '<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2023-09-10 10:01:24', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2023-09-10 10:01:24', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'woo-activation-survey-blockers-22AUG23', 'info', 'en_US', 'How can we help you get that first sale?', 'Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!', '{}', 'pending', 'woocommerce.com', '2023-09-10 10:01:24', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'wc-admin-onboarding-payments-reminder', 'info', 'en_US', 'Start accepting payments on your store!', 'Take payments with the provider that’s right for you - choose from 100+ payment gateways for WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2023-09-21 09:14:39', NULL, 0, 'plain', '', 0, 0, 'info'),
(61, 'wc_admin_BNPL_WCPay_Q3_2023', 'marketing', 'en_US', 'Afterpay and Affirm have arrived in WooCommerce Payments!', 'Give your customers more power with buy now, pay later from Afterpay and Affirm — now included as part of WooCommerce Payments! With this new integration, you can quickly offer a checkout option that converts sales and boosts average order value, all from within the same payments dashboard you already know.', '{}', 'pending', 'woocommerce.com', '2023-09-21 09:14:44', NULL, 0, 'plain', '', 0, 0, 'info'),
(62, 'wc_admin_afterpay_WCPay_Q3_2023', 'marketing', 'en_US', 'Afterpay has arrived in WooCommerce Payments!', 'Give your customers more power with buy now, pay later from Afterpay — now included as part of WooCommerce Payments! With this new integration, you can quickly offer a checkout option that converts sales and boosts average order value, all from within the same payments dashboard you already know.', '{}', 'pending', 'woocommerce.com', '2023-09-21 09:14:44', NULL, 0, 'plain', '', 0, 0, 'info'),
(63, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2023-09-21 09:14:45', NULL, 0, 'plain', '', 0, 0, 'info');
